INSERT INTO `b_search_content_text` VALUES (1,'2d46098a332210b0f498ca6d723ec1aa','СВИДЕТЕЛЬСТВО О ДОПУСКЕ К СТРОИТЕЛЬСТВУ ЗДАНИЙ И СООРУЖЕНИЙ\r\nОХОТА — ПРОМЫСЕЛ ИЛИ РАЗВЛЕЧЕНИЕ, СОСТОЯЩИЕ В ПОИСКЕ, ВЫСЛЕЖИВАНИИ, ПРЕСЛЕДОВАНИИ И ДОБЫЧЕ (ТО ЕСТЬ ПОИМКЕ ИЛИ УМЕРЩВЛЕНИИ) НЕКОТОРЫХ ВИДОВ ДИКИХ ЖИВОТНЫХ.\r\n'),(2,'eec96b565d42a662b7fb3e5af5cfd38e','СТРОИТЕЛЬНАЯ ЛИЦЕНЗИЯ\r\nНА ТЕРРИТОРИИ МОСКВЫ И МОСКОВСКОЙ ОБЛАСТИ ВОТ УЖЕ НА ПРОТЯЖЕНИИ ПРИЛИЧНОГО КОЛИЧЕСТВА ЛЕТ ДЕЙСТВУЕТ ЗАПРЕТ НА ИСПОЛЬЗОВАНИЕ СПИННИНГА И ЖИВЦОВОЙ УДОЧКИ В ПЕРИОД ДЕЙСТВИЯ ДВУХМЕСЯЧНИКА ПО ОХРАНЕ РЫБНЫХ ЗАПАСОВ. ВРЕМЯ ДЕЙСТВИЯ ДВУХМЕСЯЧНИКА С 10 АПРЕЛЯ ПО 10 ИЮНЯ ИЛИ НЕМНОГО МЕНЯЕТСЯ В ЗАВИСИМОСТИ ОТ ОСОБЕННОСТЕЙ ПОГОДНЫХ УСЛОВИЙ.\r\n'),(3,'1bd9cef315d5c353376385f96168397b','СЕРТИФИКАТ ПОЖАРНОЙ БЕЗОПАСНОСТИ\r\nСЕРТИФИКАТ ПОЖАРНОЙ БЕЗОПАСНОСТИ (ПОЖАРНЫЙ СЕРТИФИКАТ) – ЭТО ДОКУМЕНТ, ПОДТВЕРЖДАЮЩИЙ, ЧТО ПРОДУКЦИЯ СООТВЕТСТВУЕТ ТРЕБОВАНИЯМ ПОЖАРНОЙ БЕЗОПАСНОСТИ.\r\n'),(4,'935ed36f5542717cd20121e04eeb5d9b','СЕРТИФИКАТ СООТВЕТСТВИЯ НА ДЕРЕВЯННЫЕ КЛЕЕНЫЕ КОНСТРУКЦИИ\r\nСВИДЕТЕЛЬСТВО О ГОСУДАРСТВЕННОЙ РЕГИСТРАЦИИ ИЛИ ПИСЬМО О СООТВЕТСТВИИ ПРОДУКЦИИ ЕДИНЫМ САНИТАРНО-ЭПИДЕМИОЛОГИЧЕСКИМ И ГИГИЕНИЧЕСКИМ ТРЕБОВАНИЯМ – ДОКУМЕНТ, ПОДТВЕРЖДАЮЩИЙ, ЧТО ПРОДУКЦИЯ СООТВЕТСТВУЮТ УСТАНОВЛЕННЫМ ЕДИНЫМ ГИГИЕНИЧЕСКИМ НОРМАМ.\r\n'),(5,'ffac60d1f4d5d9c1b791b5bb02ff592c','СВИДЕТЕЛЬСТВО О ДОПУСКЕ СРО\r\nСЕРТИФИКАТ СООТВЕТСТВИЯ – ДОКУМЕНТ, ПОДТВЕРЖДАЮЩИЙ СООТВЕТСТВИЕ ПРОДУКЦИИ ТРЕБОВАНИЯМ КАЧЕСТВА И БЕЗОПАСНОСТИ, УСТАНОВЛЕННЫМИ ДЛЯ НЕЕ ДЕЙСТВУЮЩИМИ СТАНДАРТАМИ И ПРАВИЛАМИ (ГОСТ, ГОСТ Р, ГОСТ Р МЭК, ТУ, ТЕХНИЧЕСКИЙ РЕГЛАМЕНТ И ПР.).\r\n'),(6,'e34761138fb1eda7cfc06f6031a6dbfd','ООО \"УМНЫЙ ДОМ\"\r\nРАЗРАБОТЧИК УМНЫХ СИСТЕМ ДЛЯ ДОМОВ И КВАРТИР. \r\nНАШИ ДРУЗЬЯ ИЗ КОМПАНИИ «УМНЫЙ ДОМ» ЗНАЮТ ВСЕ О ТОМ, КАК БУДЕТ ВЫГЛЯДЕТЬ БУДУЩЕЕ. УПРАВЛЯЕМЫЕ СИСТЕМЫ ОТОПЛЕНИЯ, ВАННА, НАПОЛНЯЮЩАЯСЯ ВОДОЙ ПО НАЖАТИЮ КНОПКИ СМАРТФОНА, ИНТЕЛЛЕКТУАЛЬНАЯ СИСТЕМА ДЛЯ ПРОБУЖДЕНИЯ — ВСЕ ЭТО СИСТЕМА ИНТЕЛЛЕКТУАЛЬНОГО УПРАВЛЕНИЯ ЖИЛИЩЕМ.\r\n'),(7,'6c930860413ac3f1466ac65ecccdbf1d','\"ЗАПАДНОЕ КУКУЕВО\"\r\nОСТРОВОК СПОКОЙСТВИЯ В ЮЖНОМ БУТОВО. ГОТОВАЯ ИНФРАСТРУКТУРА И УЮТНЫЕ ДОМА ДЛЯ СЕМЕЙНЫХ ЛЮДЕЙ. \r\nЗАПАДНОЕ КУКУЕВО — КОТТЕДЖНЫЙ ПОСЕЛОК В ЮЖНОМ ПОДМОСКОВЬЕ, РАСПОЛОЖЕННЫЙ НЕПОДАЛЕКУ ОТ ГОРОДА БУТОВА. ВКЛЮЧАЕТ В СЕБЯ 40 ДОМОВ, СПРОЕКТИРОВАННЫХ И ВОЗВЕДЕННЫХ НАМИ В 2014-2015 ГОДАХ. \rИНФРАСТРУКТУРА ПОСЕЛКА ВКЛЮЧАЕТ В СЕБЯ ДЕТСКИЙ САД, НОЧНОЙ КЛУБ, ДК ПИОНЕРОВ И ШКОЛЬНИКОВ, А ТАКЖЕ БОЛЬШУЮ ХОККЕЙНУЮ КОРОБКУ, КОТОРАЯ ЛЕТОМ ПРЕВРАЩАЕТСЯ В ПОЛНОЦЕННЫЙ ФУТБОЛЬНЫЙ ПОЛИГОН.\rВ НАСТОЯЩЕЕ ВРЕМЯ ВСЕ ДОМА ПРОДАНЫ, ИДУТ ПЕРЕГОВОРЫ О ЗАСТРОЙКЕ СОСЕДНИХ ПЛОЩАДЕЙ.\r\n'),(8,'fcfc6c66a0d20b2c09e0c1d3788b4ffb','ПЕЧНОЙ МАСТЕР\r\nПЕЧИ И КАМИНЫ, СОЗДАННЫЕ С ПРИМЕНЕНИЕМ ЭКСКЛЮЗИВНЫХ МАТЕРИАЛОВ.\r\nКОГДА-ТО ПЕЧЬ БЫЛА НАСТОЯЩИМ СЕРДЦЕМ ДОМА. НА НЕЙ СПАЛИ, В НЕЙ ГОТОВИЛИ ХЛЕБ И ЩИ. ОНА ДАРИЛА ТЕПЛО И БЫЛА ОПЛОТОМ СЕМЬИ. В \"ПЕЧНОМ МАСТЕРЕ\" ЗНАЮТ, КАК СДЕЛАТЬ ВАШУ ЖИЗНЬ ЕЩЕ КОМФОРТНЕЕ – С ПОМОЩЬЮ КАМИНА ИЛИ АУТЕНТИЧНОЙ РУССКОЙ ПЕЧИ. МАСТЕРА КОМПАНИИ ИСПОЛЬЗУЮТ УСОВЕРШЕНСТВОВАННУЮ ТЕХНОЛОГИЮ ТРАДИЦИОННОЙ КЛАДКИ, ПОЭТОМУ ПЕЧИ И КАМИНЫ НЕ ЧАДЯТ, А ТОЛЬКО СОГРЕВАЮТ И РАДУЮТ ГЛАЗ.\r\n\r\nВ ИЮНЕ ВСЕМ КЛИЕНТАМ КОМПАНИИ \"ПЕЧНОЙ МАСТЕР\" ДАРИТ СКИДКУ 40% НА КАМИНЫ И ПЕЧИ. СПЕШИТЕ!\r\n'),(9,'d46ef3012853564beec2c3678369c034','МИР ГАЗОНОВ\r\nЗЕЛЕНЫЙ КОВЕР НА ВАШЕМ ПРИУСАДЕБНОМ УЧАСТКЕ. \r\nПОЛЕ ДЛЯ ГОЛЬФА, ЗЕЛЕНЫЙ ГАЗОН ДЛЯ ПИКНИКА И ДЕТСКИХ ИГР ИЛИ ШИКАРНАЯ КЛУМБА С РОДОДЕНДРОНАМИ? К ЧЕМУ МЕЛОЧИТЬСЯ, ВСЕ СРАЗУ! ФЛОРИСТЫ И ЛАНДШАФТНЫЕ ДИЗАЙНЕРЫ ИЗ КОМПАНИИ \"МИР ГАЗОНОВ\" ЗНАЮТ, КАК ЗАСТАВИТЬ ЛЮБОЙ ПРИУСАДЕБНЫЙ УЧАСТОК ЗАИГРАТЬ НОВЫМИ КРАСКАМИ. ЗА АЛЬПИЙСКИМИ ГОРКАМИ И САДАМИ КАМНЕЙ – ТОЖЕ СЮДА. ВАМ ПОНРАВИТСЯ, ОБЕЩАЕМ.\r\n'),(10,'ba46704aebbab3daad07b81973b6cfb1','ЕСЛИ ЛИ МАНУАЛ ПО РАБОТЕ С ПАНЕЛЬЮ АДМИНИСТРАТОРА? Т.К. В БОЛЬШОМ КОЛИЧЕСТВЕ НАСТРОЕК МЫ ПОТРЯЛИСЬ\r\nРЕШЕНИЕ ОСНОВАНО НА ТИПОВОМ ФУНКЦИОНАЛЕ 1С-БИТРИКС. ВСЕМ КЛИЕНТАМ МЫ РЕКОМЕНДУЕМ ПРОЙТИ ОБУЧАЮЩИЕ КУРСЫ И ОЗНАКОМИТЬСЯ С ДОКУМЕНТАЦИЕЙ ОПУБЛИКОВАННОЙ ПО АДРЕСУ HTTP://DEV.1C-BITRIX.RU/DOCS/PHP.PHP \r\nОБРАТИТЕ ВНИМАНИЕ НА ОБУЧАЮЩИЕ МАТЕРИАЛЫ ИЗ РАЗДЕЛОВ \"КОНТЕНТ-МЕНЕДЖЕРАМ\" И \"АДМИНИСТРАТОРАМ\", ОНИ ПОМОГУТ ВАМ ИСПОЛЬЗОВАТЬ ПО МАКСИМУМУ ВОЗМОЖНОСТИ 1С-БИТРИКС И НАШЕГО РЕШЕНИЯ, В ТОЖЕ ВРЕМЯ УБЕРЕГУТ ОТ ВОЗМОЖНЫХ ОШИБОК.\r\n'),(11,'dc8d90bf085e59b6ffe5de6fee947e9b','ХОЧУ КУПИТЬ РЕШЕНИЕ, НО У МЕНЯ ТОЛЬКО ДЕМО ВЕРСИЯ 1С-БИТРИКС\r\nДЛЯ ИСПОЛЬЗОВАНИЯ РЕШЕНИЯ ВАМ НЕОБХОДИМА КОПИЯ 1С-БИТРИКС С АКТИВИРОВАННОЙ ЛИЦЕНЗИЕЙ. В ПРОТИВНОМ СЛУЧАЕ НАШЕ РЕШЕНИЕ (КАК ВПРОЧЕМ И ДРУГИЕ ПЛАТНЫЕ РЕШЕНИЯ) ПРОСТО НЕ УСТАНОВИТСЯ. ПОСКОЛЬКУ КЛЮЧ РЕШЕНИЯ ПРИВЯЗЫВАЕТСЯ КЛЮЧУ 1С-БИТРИКС, ТО УСТАНОВКА ПЛАТНОГО РЕШЕНИЯ НА ДЕМОВЕРСИЮ НЕ ВОЗМОЖНА. \r\n\r\nПОЭТОМУ САМАЯ ОПТИМАЛЬНАЯ ПОСЛЕДОВАТЕЛЬНОСТЬ ДЕЙСТВИЙ:\r\n1. ПОКУПКА ЛИЦЕНЗИИ НА ПРОДУКТ 1С-БИТРИКС.\r\n2. ПОКУПКА ЛИЦЕНЗИИ НА РЕШЕНИЕ (МОЖНО СОВМЕСТИТЬ С ПУНКТОМ 1).\r\n3. АКТИВАЦИЯ ЛИЦЕНЗИИ БИТРИКСА. \r\n4. УСТАНОВКА РЕШЕНИЯ.\r\n'),(12,'ed0e6b82b5120720bd59871d3c431418','КАК КУПИТЬ БИТРИКС + РЕШЕНИЕ?\r\nКУПИТЬ 1С-БИТРИКС + РЕШЕНИЕ МОЖНО ЧЕРЕЗ НАС ИЛИ НА САЙТЕ 1С-БИТРИКС:МАРКЕТПЛЕЙС (HTTP://MARKETPLACE.1C-BITRIX.RU/SOLUTIONS/ASPRO.ISHOP/). ЦЕНА В ОБОИХ СЛУЧАЯХ БУДЕТ ОДИНАКОВОЙ. \r\n\r\nПРИ ПОКУПКЕ ЧЕРЕЗ НАС МЫ ПРОИЗВОДИМ БЕСПЛАТНУЮ УСТАНОВКУ И НАСТРОЙКУ БИТРИКС+ НАШЕ РЕШЕНИЕ. \r\n\r\nОФОРМИТЬ ПОКУПКУ ЧЕРЕЗ НАШУ КОМПАНИЮ ОЧЕНЬ ПРОСТО + В ТАКОМ СЛУЧАЕ МЫ СМОЖЕМ ПРОИЗВЕСТИ БЕСПЛАТНУЮ УСТАНОВКУ БИТРИКС+НАШЕ РЕШЕНИЕ НА ВАШ ХОСТИНГ. ХОСТИНГ ДОЛЖЕН БЫТЬ ОПТИМИЗИРОВАН ПОД РАБОТУ С 1С-БИТРИКС (К, ПРИМЕРУ ТАЙМВЕБ ТАРИФ БИТРИКС.1САЙТ HTTP://TIMEWEB.RU/SERVICES/BITRIX/ONE/).\r\n\r\nГОТОВЫ ПОДГОТОВИТЬ ВАМ СЧЕТ НА ОПЛАТУ, ПОЖАЛУЙСТА ВЫШЛИТЕ РЕКВИЗИТЫ ОРГАНИЗАЦИИ ИЛИ ПАСПОРТНЫЕ ДАННЫЕ ЕСЛИ ПОКУПКА БУДЕТ ПРОИЗВЕДЕНА НА ЧАСТНОЕ ЛИЦО.\r\n'),(13,'aae5d7713ef708efb4908f58419fb6d4','ПОКУПКА РЕШЕНИЯ БЕЗ РЕДАКЦИИ 1С-БИТРИКС\r\n1С-БИТРИКС ЯВЛЯЕТСЯ СИСТЕМОЙ УПРАВЛЕНИЯ САЙТОМ (ПЛАТФОРМОЙ НА КОТОРОЙ БУДЕТ РАБОТАТЬ САЙТ), НА НЕЁ УЖЕ УСТАНАВЛИВАЮТСЯ ГОТОВЫЕ РЕШЕНИЯ НАПРИМЕР: \"АСПРО. ИНТЕРНЕТ МАГАЗИН\".\r\n\r\nДЛЯ ЗАПУСКА ИНТЕРНЕТ-МАГАЗИНА ВАМ НЕОБХОДИМО ПРИОБРЕТИ 1С-БИТРИКС РЕДАКЦИЙ МАЛЫЙ БИЗНЕС ИЛИ БИЗНЕС + РЕШЕНИЕ ИЗ МАРКЕТПЛЕЙС. \r\n\r\nБУДЕМ РАДЫ ЗА ВЫБОР РЕШЕНИЙ ОТ КОМПАНИИ АСПРО!\r\n'),(14,'f9db2125e621b92ac6d19f82bfda33a8','ЦЕНА ПРОДУКТА НА САЙТЕ УКАЗАНА С УЧЕТОМ НДС?\r\nЭЛЕКТРОННЫЕ ВЕРСИИ ПРОДУКТОВ НЕ ОБЛАГАЮТСЯ НДС В СВЯЗИ СО ВСТУПЛЕНИЕМ В СИЛУ С 01 ЯНВАРЯ 2008 Г. ЧЕТВЕРТОЙ ЧАСТИ ГРАЖДАНСКОГО КОДЕКСА РФ, СТАТЬИ 1 ФЕДЕРАЛЬНОГО ЗАКОНА ОТ 19.07.2007 N 195-ФЗ \"О ВНЕСЕНИИ ИЗМЕНЕНИЙ В ОТДЕЛЬНЫЕ ЗАКОНОДАТЕЛЬНЫЕ АКТЫ РОССИЙСКОЙ ФЕДЕРАЦИИ В ЧАСТИ ФОРМИРОВАНИЯ БЛАГОПРИЯТНЫХ НАЛОГОВЫХ УСЛОВИЙ ДЛЯ ФИНАНСИРОВАНИЯ ИННОВАЦИОННОЙ ДЕЯТЕЛЬНОСТИ\".\r\n'),(15,'eeb49b4510f7c9ca35d8bfdba8eb3763','ЧТО ЗНАЧИТ \"ЛИЦЕНЗИЯ ПОЗВОЛЯЕТ СОЗДАТЬ ДВА САЙТА\"?\r\nНА ОСНОВЕ ОДНОГО ЭКЗЕМПЛЯРА ПРОДУКТА МОЖНО СОЗДАТЬ ДВА САЙТА В РАМКАХ ОДНОГО ВЕБ-ПРОЕКТА. ЕСЛИ САЙТОВ В ПРОЕКТЕ БОЛЬШЕ ДВУХ, НЕОБХОДИМО ДОКУПИТЬ ЛИЦЕНЗИИ НА САЙТЫ, СТОИМОСТЬ КОТОРЫХ ЗАВИСИТ ОТ РЕДАКЦИИ: HTTP://WWW.1C-BITRIX.RU/BUY/CMS.PHP#TAB-SITES-LINK.\r\n\r\nТАКИЕ САЙТЫ РАБОТАЮТ НА ОДНОМ ЭКЗЕМПЛЯРЕ БАЗЫ ДАННЫХ И ИМЕЮТ ОДИН АДМИНИСТРАТИВНЫЙ ИНТЕРФЕЙС. А ЗНАЧИТ, АДМИНИСТРАТОР СИСТЕМЫ ЯВЛЯЕТСЯ АДМИНИСТРАТОРОМ ВСЕХ САЙТОВ, РАБОТАЮЩИХ НА ОДНОЙ КОПИИ ПРОДУКТА.\r\n\r\n\r\nВАЖНО НЕ ПУТАТЬ ПОНЯТИЯ \"САЙТ\" И \"КОПИЯ ПРОДУКТА\" (\"УСТАНОВКА ПРОДУКТА\"). НЕСКОЛЬКО САЙТОВ НАСТРАИВАЮТСЯ НА ОДНОЙ УСТАНОВКЕ. ПРИ ЭТОМ ЛИЦЕНЗИЯ ДОПУСКАЕТ НАЛИЧИЕ ДВУХ УСТАНОВОК, ОДНА ИЗ КОТОРЫХ ПРЕДНАЗНАЧЕНА ДЛЯ РАЗРАБОТКИ И НЕ ДОСТУПНА ИЗ ИНТЕРНЕТА.\r\n\r\n\r\nОБЩИМИ ДЛЯ САЙТОВ ЯВЛЯЮТСЯ: \r\n- БАЗА ПОЛЬЗОВАТЕЛЕЙ,\r\n- НАСТРОЙКИ МОДУЛЕЙ (И САМИ МОДУЛИ),\r\n- ШАБЛОНЫ САЙТОВ,\r\n- СИСТЕМА АВТОРИЗАЦИИ\r\nИ ДР.\r\n'),(16,'91a9a759352cea467c45e64907a7271f','ПЕТРОВ АЛЕКСЕЙ ПЕТРОВИЧ\r\n\r\n'),(17,'9621573a2e3f072e51343c1db35e3aa6','ЕЛЕНА СИЛИНА\r\n\r\n'),(18,'34de6af22ae7001400dfea1f5607b928','АЛЕНА КУЗНЕЦОВА\r\n\r\n'),(19,'19b8e5c977afc8ba17cf00fb9a50acfb','ТАТЬЯНА МАКАРОВА\r\n\r\n'),(20,'37862cf2ba2479eb613b8fc96aa8087e','ПРОРАБ\r\nКОНТРОЛИРОВАТЬ СТРОИТЕЛЕЙ И СЛЕДИТЬ ЗА КАЧЕСТВОМ РАБОТ – ОСНОВНЫЕ ОБЯЗАННОСТИ ЭТОГО УНИКАЛЬНОГО ЧЕЛОВЕКА. \r\n\rОБЯЗАННОСТИ:\rОРГАНИЗОВЫВАТЬ РАБОТЫ НА ОБЪЕКТЕ: ЗАКЛАДКА ФУНДАМЕНТА, ЗЕМЛЯНЫЕ РАБОТЫ, ОТДЕЛКА, НЕПОСРЕДСТВЕННО СТРОИТЕЛЬСТВО \rОРГАНИЗОВЫВАТЬ РАБОТУ НАШИХ ЗАМЕЧАТЕЛЬНЫХ СТРОИТЕЛЕЙ НА ОБЪЕКТЕ \rСЛЕДИТЬ ЗА ХОДОМ РАБОТ — БРИГАДЕ ДОЛЖНО ВСЕГДА ХВАТАТЬ МАТЕРИАЛОВ, А ОБЪЕКТЫ ДОЛЖНЫ СДАВАТЬСЯ ТОЧНО В СРОК\rКОНТРОЛИРОВАТЬ КАЧЕСТВО: МЫ ДОРОЖИМ ДОВЕРИЕМ НАШИХ КЛИЕНТОВ, ПОЭТОМУ В ГОТОВОМ ДОМЕ НЕ ДОЛЖНО БЫТЬ КРИВЫХ СТЫКОВ И НЕБРЕЖНО УЛОЖЕННОЙ ПЛИТКИ\rОБЩАТЬСЯ С ЗАКАЗЧИКАМИ, ГОСУДАРСТВЕННЫМИ И ЭКСПЛУАТИРУЮЩИМИ ОРГАНИЗАЦИЯМИ\rКООРДИНИРОВАТЬ НАШИХ СУБПОДРЯДЧИКОВ — ИНТЕРЬЕРНЫХ РАБОТНИКОВ И ИНЖЕНЕРОВ \rВЕСТИ ПРОИЗВОДСТВЕННУЮ ДОКУМЕНТАЦИЮ И ДОКУМЕНТАЦИЮ ПО ОХРАНЕ ТРУДА\rСЛЕДИТЬ ЗА СОБЛЮДЕНИЕМ НОРМ ОХРАНЫ ТРУДА И ТЕХНИКИ БЕЗОПАСНОСТИ.\rТРЕБОВАНИЯ:\rОПЫТ РАБОТЫ НА ОБЪЕКТАХ МАЛОЭТАЖНОГО СТРОИТЕЛЬСТВА \rЗНАНИЕ ТЕХНОЛОГИЙ ОБЩЕСТРОИТЕЛЬНЫХ И ОТДЕЛОЧНЫХ РАБОТ \rОПЫТ УПРАВЛЕНИЯ СТРОИТЕЛЬНЫМИ БРИГАДАМИ НЕ МЕНЕЕ 20 ЧЕЛОВЕК\rВЫСШЕЕ ТЕХНИЧЕСКОЕ ОБРАЗОВАНИЕ — АРХИТЕКТУРНО-СТРОИТЕЛЬНОЕ.\rУСЛОВИЯ\rОФОРМЛЕНИЕ ПО ТК РФ\rФИКСИРОВАННЫЙ ОКЛАД + % ОТ СТОИМОСТИ ПРОЕКТА.\r\n'),(21,'206393128ad278dd9d8406ae5b066351','МЕНЕДЖЕР\r\nИЩЕМ ЧЕЛОВЕКА, КОТОРЫЙ БУДЕТ ОБЩАТЬСЯ С КЛИЕНТАМИ, ВЫЯВЛЯТЬ ПОТРЕБНОСТИ И ПОМОГАТЬ ИМ ОБРЕСТИ ДОМ МЕЧТЫ.\r\n\rУСЛОВИЯ \rОБЩАТЬСЯ С КЛИЕНТАМИ И ВЫЯВЛЯТЬ ИХ КЛЮЧЕВЫЕ ПОТРЕБНОСТИ\rКООРДИНИРОВАТЬ ПОДРЯДЧИКОВ И СТРОИТЕЛЬНУЮ БРИГАДУ\rГОТОВИТЬ И ОФОРМЛЯТЬ ПРОЕКТНУЮ ДОКУМЕНТАЦИЮ СОВМЕСТНО С ПРОЕКТИРОВЩИКАМИ.\rТРЕБОВАНИЯ \rВЫСШЕЕ ОБРАЗОВАНИЕ, ЖЕЛАТЕЛЬНО В СФЕРЕ СТРОИТЕЛЬСТВА\rОРГАНИЗОВАННОСТЬ И ДИСЦИПЛИНА\rУМЕНИЕ НАХОДИТЬ ОБЩИЙ ЯЗЫК И ОБЩАТЬСЯ С ЛЮБЫМИ КЛИЕНТАМИ\rЗНАНИЕ ТЕНДЕНЦИЙ СОВРЕМЕННОГО МАЛОЭТАЖНОГО СТРОИТЕЛЬСТВА. \rУСЛОВИЯ:\rРАБОТА В КОМПАНИИ-ЛИДЕРЕ НА РЫНКЕ МАЛОЭТАЖНОГО СТРОИТЕЛЬСТВА В РОССИИ\rУДОБНЫЙ ГРАФИК И ОТЛИЧНАЯ ЗАРПЛАТА\rСКИДКА 40% НА СОБСТВЕННЫЙ ДОМ ПОСЛЕ 3 ЛЕТ РАБОТЫ В КОМПАНИИ.\r\n'),(40,'61805d18b44baaa504a70a12aa01f1dd','ТОРГОВЫЙ ДОМ\r\nРАЗРАБАТЫВАЕМ ПРОЕКТЫ МАЛОЭТАЖНЫХ ЖИЛЫХ ДОМОВ И ПРОМЫШЛЕННЫХ ОБЪЕКТОВ \r\n\rТОРГОВЫЙ ДОМ\rДЛЯ КАЖДОГО ИЗ НАС СВОЙ ДОМ – ЭТО ТОТ СОБСТВЕННЫЙ МАЛЕНЬКИЙ МИРОК, НАХОДЯСЬ В КОТОРОМ МЫ ЧУВСТВУЕМ СЕБЯ КОМФОРТНО И ЧТО САМОЕ ВАЖНОЕ- В БЕЗОПАСНОСТИ. ЕЩЕ НАШИ ПРЕДКИ ГОВОРИЛИ: «МОЙ ДОМ – МОЯ КРЕПОСТЬ». ОНИ ТАКЖЕ ПОЛАГАЛИ, ЧТО КАЖДЫЙ МУЖЧИНА В СВОЕЙ ЖИЗНИ ДОЛЖЕН СДЕЛАТЬ ТРИ ВСЕМ ИЗВЕСТНЫХ ДЕЛА, ОДНИМ ИЗ КОТОРЫХ ЯВЛЯЕТСЯ ПОСТРОИТЬ СОБСТВЕННЫЙ ДОМ.\rИ ВОТ КОГДА НАСТУПАЕТ ТАКОЙ ВАЖНЫЙ И ЗНАМЕНАТЕЛЬНЫЙ МОМЕНТ В ЖИЗНИ КАЖДОГО ИЗ МУЖЧИН, ИМ ПРИХОДИТСЯ РЕШАТЬ ЕЩЕ ОДНУ СЛОЖНУЮ ПРОБЛЕМУ. КАКИМ ВИДОМ СТРОИТЕЛЬНЫХ МАТЕРИАЛОВ ОБУСТРОИТЬ КРЫШУ, ФАСАД ВОЗВОДИМОГО ДОМА ИЛИ КОТТЕДЖА. КАЖДЫЙ ИЗ НАС НА ЭТОТ СЧЕТ ИМЕЕТ СВОИ ПРЕДСТАВЛЕНИЯ И ФАНТАЗИИ.\rКАЖДЫЙ МАТЕРИАЛ ОБЛАДАЕТ МАССОЙ ДОСТОИНСТВ, И ВЫ ВСЕГДА НАЙДЕТЕ НАИБОЛЕЕ ПРИЕМЛЕМЫЙ ДЛЯ ВАС. БОЛЕЕ ПОДРОБНО ОЗНАКОМИТЬСЯ С ПРЕИМУЩЕСТВАМИ КАЖДОГО ВИДА КРОВЛИ, УЗНАТЬ РАСЦЕНКИ И ПОДРОБНЕЕ УЗНАТЬ ОБ УСЛУГАХ, КОТОРЫЕ ПРЕДОСТАВЛЯЕТ НАША КОМПАНИЯ, ВЫ МОЖЕТЕ У МЕНЕДЖЕРОВ, ПОЗВОНИВ В ОФИС НАШЕЙ КОМПАНИИ ПО ТЕЛЕФОНУ (3952) 50-00-50.\r\n'),(41,'92f4fe233a4c2bdc85c670b98fed78e9','СТРОИТЕЛЬНАЯ КОМПАНИЯ\r\nРАБОТЫ ЛЮБОЙ СЛОЖНОСТИ - ОТ ПРОЕКТИРОВАНИЯ ДО РЕСТАВРАЦИИ СТАРИННЫХ ЗДАНИЙ\r\n\rСТРОИТЕЛЬНАЯ КОМПАНИЯ\rСТРОИТЕЛЬНАЯ КОМПАНИЯ \r«БАЙС»\r - СПЕЦИАЛИЗИРОВАННОЕ ПРЕДПРИЯТИЕ, ВКЛЮЧАЮЩИЕ В СЕБЯ СЛЕДУЮЩИЕ НАПРАВЛЕНИЯ РАБОТЫ: МОНТАЖ, РЕКОНСТРУКЦИЯ И РЕМОНТ ПЛОСКИХ, СКАТНЫХ, МАНСАРДНЫХ КРОВЕЛЬ, ВЕНТИЛИРУЕМЫХ ФАСАДОВ.\rНАЛИЧИЕ СОЛИДНОЙ ПРОИЗВОДСТВЕННОЙ БАЗЫ, ТРАНСПОРТА И СКЛАДСКИХ ПОМЕЩЕНИЙ ПОЗВОЛЯЕТ УСПЕШНО КОНКУРИРОВАТЬ С ПОДОБНЫМИ ОРГАНИЗАЦИЯМИ ПО ЦЕНЕ, СРОКАМ, А ГЛАВНОЕ КАЧЕСТВУ ВЫПОЛНЯЕМЫХ РАБОТ.\rМЫ РАБОТАЕМ ДЛЯ ТЕХ, КТО ЗАИНТЕРЕСОВАН В БЫСТРОМ, ТЕХНИЧЕСКИ ГРАМОТНОМ И КАЧЕСТВЕННОМ РЕШЕНИИ ВОПРОСОВ, СВЯЗАННЫХ С РЕКОНСТРУКЦИЕЙ, РЕМОНТОМ ИЛИ СТРОИТЕЛЬСТВОМ НОВЫХ КРОВЕЛЬНЫХ КОНСТРУКЦИЙ ЛЮБОГО НАЗНАЧЕНИЯ: ОТ ЭКСКЛЮЗИВНЫХ КОТТЕДЖЕЙ ДО АДМИНИСТРАТИВНЫХ ЗДАНИЙ И ПРОМЫШЛЕННЫХ ПРЕДПРИЯТИЙ.\r \rМЫ МОЖЕМ ПРЕДЛОЖИТЬ ВАШИМ КЛИЕНТАМ СЛЕДУЮЩИЕ УСЛУГИ ПО МОНТАЖУ И РЕКОНСТРУКЦИИ:\rКРОВЕЛЬНЫЕ РАБОТЫ (ПЛОСКИЕ, СКАТНЫЕ, МАНСАРДНЫЕ)\rМОНТАЖ ВОДОСТОЧНОЙ СИСТЕМЫ\rМОНТАЖ ВЕНТИЛИРУЕМЫХ ФАСАДОВ\rУТЕПЛЕНИЕ И ПАРОИЗОЛЯЦИЯ КРОВЛИ И ФАСАДА\rАНТИКОРРОЗИЙНАЯ ЗАЩИТА МЕТАЛЛОКОНСТРУКЦИЙ\rОГНЕБИОЗАЩИТА\rУСТРОЙСТВО ПРОМЫШЛЕННЫХ ПОЛОВ\rСТРОИТЕЛЬСТВО БЫСТРОВОЗВОДИМЫХ ЗДАНИЙ\r\n'),(42,'eac905310905443aaf00dddff3b06d78','ПРОИЗВОДСТВО\r\nПРОФЕССИОНАЛЬНАЯ ВНУТРЕННЯЯ ОТДЕЛКА ПОМЕЩЕНИЙ, ЧЕТКОЕ СООТВЕТСТВИЕ ДИЗАЙН ПРОЕКТУ.\r\n\rПРОИЗВОДСТВО МЕТАЛЛОИЗДЕЛИЙ\rВ 2007 ГОДУ КОМПАНИЯ «БАЙС» МОДЕРНИЗИРОВАЛА ПРОИЗВОДСТВО ПО МЕТАЛЛООБРАБОТКЕ И ИЗГОТОВЛЕНИЮ ИЗДЕЛИЙ ИЗ ТОНКОЛИСТОВОГО МЕТАЛЛА – ВЕНТИЛЯЦИОННЫХ ЗАГОТОВОК, ВОДОСТОЧНЫХ СИСТЕМ, ФАЛЬЦЕВОЙ КРОВЛИ И КРОВЕЛЬНЫХ КОМПЛЕКТУЮЩИХ. ТАКЖЕ БЫЛО ПРИОБРЕТЕНО ОБОРУДОВАНИЕ ДЛЯ ПЛАЗМЕННОЙ РЕЗКИ И РАСКРОЯ ЛИСТОВОГО МЕТАЛЛА. ИЗДЕЛИЯ ИЗГОТАВЛИВАЮТСЯ НА ИМПОРТНОМ ЭЛЕКТРОННОМ ОБОРУДОВАНИИ МАРКИ «SСHEСHTL» (ГЕРМАНИЯ), «SPIRO» (ШВЕЙЦАРИЯ), «RAS» (ГЕРМАНИЯ), «P.E.I.POINT» (ИТАЛИЯ), «MERCUR» (ИТАЛИЯ).\rМОДЕРНИЗИРОВАННОЕ ПРОИЗВОДСТВО ПОЗВОЛЯЕТ ВЫПУСКАТЬ ВОЗДУХОВОДЫ ДЛЯ ВЕНТИЛЯЦИОННЫХ СИСТЕМ И КОМПЛЕКТУЮЩИЕ ДЛЯ КРОВЛИ И ФАСАДА В 5 РАЗ БОЛЬШЕ И БЫСТРЕЕ, ЧЕМ ПРИ РУЧНОМ ИЗГОТОВЛЕНИИ.\r\n'),(43,'040e95f7e8e3a46ba433ef34b1f77350','СПЕЦИАЛЬНОЕ ПРЕДЛОЖЕНИЕ НА ОКОННЫЕ СИСТЕМЫ\r\nЕВРОПЕЙСКИЕ ОКНА ПО ДОСТУПНЫМ ЦЕНАМ. ТОЛЬКО В ЯНВАРЕ! \r\n\rХОРОШИЕ ОКНА СПОСОБНЫ НА МНОГОЕ.\rОНИ ЗАЩИЩАЮТ НАС ОТ СКВОЗНЯКА, НЕ ДАЮТ ПОЛУЧИТЬ ТЕПЛОВОЙ УДАР В ЖАРКУЮ ПОГОДУ И КОНЕЧНО, ГРЕЮТ НАС СУРОВЫМИ ЗИМАМИ, ОБЕРЕГАЯ ОТ МЕТЕЛЕЙ, СНЕГОПАДА И ПРОЧИХ ОСАДКОВ. А ЕЩЕ ЧЕРЕЗ НИХ МЫ СМОТРИМ НА МИР.\rЯНВАРЬ – ЭТО ВРЕМЯ, КОГДА МЫ ЗАПАСАЕМСЯ МАТЕРИАЛАМИ НА ВЕСЕННЕ-ЛЕТНИЙ СТРОИТЕЛЬНЫЙ СЕЗОН. ЗАКУПАЕМ ШТУКАТУРКУ ИЗ ВЕНЕЦИИ, ВЕЗЕМ ГВОЗДИ ИЗ САРАТОВА, А ИЗ ИТАЛИИ К НАМ САМОЛЕТОМ ДОСТАВЛЯЮТСЯ ОКНА ИЗ ЦЕЛЬНОГО ДЕРЕВА. СПЕШИТЕ ЗАКАЗАТЬ ИХ ПО ЛЬГОТНОЙ ЦЕНЕ!\rПОДРОБНОСТИ – У НАШИХ МЕНЕДЖЕРОВ ПО ТЕЛЕФОНУ (495) 999-99-99. СПЕШИТЕ! ПРЕДЛОЖЕНИЕ ОГРАНИЧЕНО.\r\n'),(47,'888b58f7cf03172e9f2f24938c29c36c','СКОЛЬКО СТОИТ ДОМ ПОСТРОИТЬ\r\nБЕСПРИСТРАСТНО О ТОМ, ИЗ ЧЕГО СКЛАДЫВАЕТСЯ СТОИМОСТЬ ТАУНХАУСА, КОТТЕДЖА ИЛИ БЕСЕДКИ\r\n\rСРАЗУ ОГОРЧИМ: ПОНЯТЬ НА ГЛАЗОК, СКОЛЬКО БУДЕТ СТОИТЬ ДОМ МЕЧТЫ, НЕВОЗМОЖНО. ПРЕЖДЕ ЧЕМ СООБЩИТЬ ТОЧНУЮ СТОИМОСТЬ ТАУНХАУСА ИЛИ КОТТЕДЖА, МЫ РАЗРАБАТЫВАЕМ ПОЛНЫЙ РАБОЧИЙ ПРОЕКТ И СОСТАВЛЯЕМ СМЕТУ. В НЕЕ ВКЛЮЧЕНЫ РАБОТЫ НА КАЖДОМ ЭТАПЕ СТРОЙКИ – ОТ ЗЕМЛЯНЫХ РАБОТ ДО РАССТАНОВКЕ ГОРШКОВ С ЦВЕТАМИ НА ПОДОКОННИКЕ.\rКОНЕЧНО ЖЕ, КЛИЕНТЫ НАС ЧАСТО ТОРОПЯТ: «СМОТРИТЕ, КАКАЯ ХОРОШАЯ ПОГОДА! САМОЕ ВРЕМЯ ЗАКЛАДЫВАТЬ ФУНДАМЕНТ, ПОКА НЕ НАСТАЛ СЕЗОН ДОЖДЕЙ», «А ВОТ В \"МЕРУА ЛЕРМЕН\" СЕЙЧАС СКИДКИ НА ЛАМИНАТ, ЗАВТРА БУДЕТ ДОРОЖЕ». НО МЫ СТОИЧЕСКИ СОПРОТИВЛЯЕМСЯ НАПОРУ. ЧЕМ ПОДРОБНЕЕ БУДЕТ ПРОСЧИТАН ПРОЕКТ, ТЕМ МЕНЬШЕ БУДЕТ РАЗОЧАРОВАНИЙ ПОСЛЕ.\rЧАСТО БЫВАЕТ ТАК, ЧТО МЫ НАЗЫВАЕМ ОРИЕНТИРОВОЧНУЮ ЦЕНУ, А ПОТОМ У ЗАКАЗЧИКА ПОЯВЛЯЮТСЯ ДОПОЛНИТЕЛЬНЫЕ ПОЖЕЛАНИЯ – ОДНО, ДРУГОЕ, ТРЕТЬЕ. ОДНАЖДЫ ВЫШЛО ТАК, ЧТО СТОИМОСТЬ ДОМА ВЫРОСЛА В 10 РАЗ. МУЖЧИНА ХОТЕЛ ДОМ ИЗ ПЕНОБЛОКОВ, А В ПРОЦЕССЕ РЕШИЛ, ЧТО ЕМУ МИЛЕЕ ОЦИЛИНДРОВАННЫЕ БРЕВНА. А В ДРУГОМ СЛУЧАЕ ПОЖИЛАЯ ПАРА СТРОИЛА САДОВЫЙ ДОМ И ВЫСКАЗАЛА ПОЖЕЛАНИЕ, ЧТОБЫ СТРОЙКА БЫЛА КАК МОЖНО БОЛЕЕ БЮДЖЕТНОЙ. МЫ ПОДОБРАЛИ ОТОПИТЕЛЬНЫЙ КОТЕЛ ЗА 50 ТЫСЯЧ РУБЛЕЙ, НО ПОЗЖЕ, ВЗВЕСИВ ВСЕ ПЛЮСЫ И МИНУСЫ, ПРИОБРЕЛА БОЛЕЕ НАДЕЖНЫЙ И КАК СЛЕДСТВИЕ, БОЛЕЕ ДОРОГОЙ ВАРИАНТ.\rПРИ РАСЧЕТЕ СТОИМОСТИ СТРОЙКИ ДОМА МЫ ПРЕДПОЧИТАЕМ ОРИЕНТИРОВАТЬСЯ НА СТОИМОСТЬ МАТЕРИАЛОВ. К ПРИМЕРУ, ДОМ ИЗ КЛЕЕНОГО БРУСА, САМОГО СОВЕРШЕННОГО МАТЕРИАЛА, ОБОЙДЕТСЯ НЕ МЕНЕЕ ЧЕМ В 24 ТЫСЯЧИ РУБЛЕЙ ЗА КУБОМЕТР. РУБЛЕНАЯ ТОПОРОМ ИЗБУШКА - 10-12 ТЫСЯЧ РУБЛЕЙ ЗА КУБОМЕТР. А САМЫМ НЕДОРОГИМ ИЗ КАТЕГОРИИ ДЕРЕВЯННЫХ ДОМОВ БУДЕТ ОЦИЛИНДРОВАННЫЙ БРУС. СТОИМОСТЬ ДОМОВ ИЗ ЭТОГО МАТЕРИАЛА ОБЫЧНО НЕ ПРЕВЫШАЕТ 7-9 ТЫСЯЧ РУБЛЕЙ ЗА КУБОМЕТР. ОДНАКО ТУТ СТОИТ ПОМНИТЬ, ЧТО ТАКОМУ КОТТЕДЖУ ПОТРЕБУЕТСЯ 1-1,5 ГОДА НА УСАДКУ. ИМЕННО НАСТОЛЬКО БУДУТ ОТЛОЖЕНЫ ОТДЕЛОЧНЫЕ РАБОТЫ И СДАЧА ОБЪЕКТА В ЭКСПЛУАТАЦИЮ.\rПОЛНЫЙ СПИСОК СТОИМОСТИ НАШИХ УСЛУГ ВЫ МОЖЕТЕ НАЙТИ \rЗДЕСЬ.\rА ЕСЛИ У ВАС ЕСТЬ ВОПРОСЫ, НАПИШИТЕ НАМ! ПРОКОНСУЛЬТИРУЕМ И РАССЧИТАЕМ ПРИМЕРНУЮ СТОИМОСТЬ ВАШЕГО ПРОЕКТА.\r\n'),(49,'626a5d1bd18fcc622fbe4e4e3619a88a','НОВОСТИ\r\n\r\n'),(50,'f4ed20a8e47bbd0c197f0ca18ce830b7','ВОПРОСЫ И ОТВЕТЫ\r\nВ ДАННОМ РАЗДЕЛЕ ПРИВЕДЕНЫ ОТВЕТА НА ЧАСТО ЗАДАВАЕМЫЕ ВОПРОСЫ ПОСЕТИТЕЛЕЙ ВАШЕГО БУДУЩЕГО САЙТА. ИСПОЛЬЗОВАНИЕ ДАННОГО РАЗДЕЛА ПОЗВОЛИТ СОКРАТИТЬ НАГРУЗКУ НА ОПЕРАТОРОВ И ПОВЫСИТЬ УДОВЛЕТВОРЕННОСТЬ ВАШИХ КЛИЕНТОВ. \r\n'),(51,'59b47874863247635bab2bfbb9bd1ed1','ИНФОРМАЦИЯ\r\nИНФОРМАЦИЯ ДЛЯ КЛИЕНТОВ КОМПАНИИ \rВ ЭТОМ РАЗДЕЛЕ МЫ СОБРАЛИ ПОЛЕЗНУЮ ИНФОРМАЦИЮ, КОТОРАЯ ВАМ МОЖЕТ ПРИГОДИТЬСЯ. \rАКЦИИ\r— ПОДРАЗДЕЛ, В КОТОРОМ СОБРАНЫ НАШИ СЕЗОННЫЕ СПЕЦПРЕДЛОЖЕНИЯ, СКИДКИ НА РАБОТЫ И ПРОГРАММА ЛОЯЛЬНОСТИ ДЛЯ ПОСТОЯННЫХ КЛИЕНТОВ.\rНОВОСТИ\r— САМАЯ СВЕЖАЯ ИНФОРМАЦИЯ О НАШЕЙ РАБОТЕ: НОВИНКИ В ПОРТФОЛИО, ЖИЗНЬ НАШИХ СОТРУДНИКОВ, ИНТЕРЕСНЫЕ ИНФОПОВОДЫ ИЗ МИРА МАЛОЭТАЖНОГО СТРОИТЕЛЬСТВА, АРХИТЕКТУРЫ И ДИЗАЙНА ИНТЕРЬЕРОВ.\nСТАТЬИ \r— ПОДРАЗДЕЛ, В КОТОРОМ МЫ ДЕЛИМСЯ СЕКРЕТАМИ НАШЕГО МАСТЕРСТВА И ПОДРОБНО РАССКАЗЫВАЕМ, КАК ВЫПОЛНЯЕМ ТЕ ИЛИ ИНЫЕ РАБОТЫ.\nВОПРОС-ОТВЕТ \r— ПОДРАЗДЕЛ, В КОТОРОМ СОБРАНЫ САМЫЕ ЧАСТОЗАДАВАЕМЫЕ ВОПРОСЫ. СКОЛЬКО СТОИТ ДОМ ПОСТРОИТЬ, КАК МЫ РАБОТАЕМ, ГАРАНТИИ И ВОТ ЭТО ВСЕ.\rВОЗМОЖНОСТИ\r— ЗАСЕКРЕЧЕННЫЙ РАЗДЕЛ ДЛЯ ТЕХ, КТО ХОЧЕТ, ЧТОБЫ ИХ САЙТ МОГ БОЛЬШЕ.\r\n'),(52,'a85d95f23f7bfe41efb8858847f5fbbb','СТАТЬИ\r\n\r\n'),(53,'f3ff684915459e6469eaaaeceec4a9aa','КНОПКИ\r\nBUTTONS\rDEFAULT WHITE\rDEFAULT\rPRIMARY\rSUCCESS\rINFO\rWARNING\rDANGER\rTRANSPARENT\rLINK\rBUTTONS DISABLED\rDEFAULT BUTTON\rPRIMARY BUTTON\rBUTTONS WITH ICON\rORDER PROJECT\rORDER PROJECT\rALL NEWS\rALL NEWS\rBUTTONS SIZES\rLARGE BUTTON\rLARGE BUTTON\rDEFAULT BUTTON\rDEFAULT BUTTON\rSMALL BUTTON\rSMALL BUTTON\rEXTRA SMALL BUTTON\rEXTRA SMALL BUTTON\r\n'),(54,'f6dd56553e1f8d1e14ee4796a72b44e7','ОФОРМЛЕНИЕ\r\nЗАГОЛОВОК H1\rPELLENTESQUE PELLENTESQUE EGET TEMPOR TELLUS. FUSCE LACLLENTESQUE EGET TEMPOR TELLUS ELLENTESQUE PELLEINIA TEMPOR MALESUADA. PELLENTESQUE PELLENTESQUE EGET TEMPOR TELLUS ELLENTESQUE PELLENTESQUE EGET TEMPOR TELLUS. FUSCE LACINIA TEMPOR MALESUADA.\rЗАГОЛОВОК H2\rPELLENTESQUE PELLENTESQUE USCE LACLLENTESQUE EGET TEMPOR TELLUS ELLENTESQUE PELLEINIA TEMPOR MALESUADA. PELLENTESQUE PELLENTESQUE EGET TEMPOR 